template.script:
$("#jalur_masuk").change(function(){
    if($(this).val()!=1){
        $(".non_baru").show();
        $("#asal_universitas").prop("required", true);
        $("#fakultas_asal_id").prop("required", true);
        $("#prodi_asal_id").prop("required", true);
        $("#nim_asal").prop("required", true);
        $("#asal_alamat_univ").prop("required", true);
        $("#asal_website_univ").prop("required", true);
        $("#jenjang_id").prop("required", true);
    }else{
        $(".non_baru").hide();
        $("#asal_universitas").prop("required", false);
        $("#fakultas_asal_id").prop("required", false);
        $("#prodi_asal_id").prop("required", false);
        $("#nim_asal").prop("required", false);
        $("#asal_alamat_univ").prop("required", false);
        $("#asal_website_univ").prop("required", false);
        $("#jenjang_id").prop("required", false);                
    }
});


$('#photo').change(function(){
    var photo=this.files[0]

    if (photo.size > 300000 || photo.fileSize > 300000)
    {
        //show an alert to the user
        alert("Ukuran photo yang bisa di upload max 300 kb")

        //reset file upload control
        this.value = null;
        }
});

$(function(){
    $("#datepicker").datepicker({
        changeYear:true,
        yearRange: "1960:2060"
    });
});

$( document ).ready(function() {
    $("#jalur_masuks").change(function(){
        if($(this).val()=='prestasi'){
            $(".nilai_sma").show();
            $("#smt1").prop("required", true);
            $("#smt2").prop("required", true);
            $("#smt3").prop("required", true);
            $("#smt4").prop("required", true);
            $("#smt5").prop("required", true);
            $("#un").prop("required", true);
            $("#prestasi_file").prop("required", true);
        }else{
            $(".nilai_sma").hide();
            $("#smt1").prop("required", false);
            $("#smt2").prop("required", false);
            $("#smt3").prop("required", false);
            $("#smt4").prop("required", false);
            $("#smt5").prop("required", false);
            $("#un").prop("required", false);
            $("#prestasi_file").prop("required", false);
        }
    });

    $("#hubungans").change(function(){
        if($(this).val()=='umum'){
            $(".hub_istn1").show();
            $(".hub_istn2").hide();
            $(".hub_istn3").hide();
            $(".hub_istn4").hide();

        }else if($(this).val()=='ortu'){
            $(".hub_istn2").show();
            $(".hub_istn1").hide();
            $(".hub_istn3").hide();
            $(".hub_istn4").hide();

        }else if($(this).val()=='cikini'){
            $(".hub_istn3").show();
            $(".hub_istn1").hide();
            $(".hub_istn2").hide();
            $(".hub_istn4").hide();

        }else if($(this).val()=='karyawan'){
            $(".hub_istn4").show();
            $(".hub_istn1").hide();
            $(".hub_istn3").hide();
            $(".hub_istn2").hide();
        }
    });

    if($("#jalur_daftar").text()=="Baru"){
        $("#transkrip_file").hide();
        $("#transkrip_file").prop("required", true);
    }
});
